charX = int(w/2 - 16)
charY = int(h/2 - 16)
platX = int(w/2 - 16)
platY = int(h/2 - 16 + 32*0.75)
vy = 0

def draw():
    global charX
    global charY
    global vy

    bg = TFTColor(255, 255, 255)
    for i in range(len(screen)/2):
        screen[i*2+0] = bg >> 8
        screen[i*2+1] = bg

    if not button.value():
        charY -= 5
        vy = 0

    if (charY < h/2 - 16):
        vy -= 1
        charY -= vy
    else:
        charY = int(h/2 - 16)
        vy = 0

    for x in range(32):
        for y in range(32):
            for (img, fx, fy) in [(flr, platX, platY), (bkr, charX, charY)]:
                pix = img[x*32 + y]
                if len(pix) < 3 or pix[3] < 255:
                    continue
                col = TFTColor(pix[0], pix[1], pix[2])
                fy += x
                fx += y
                screen[(fy*w + fx)*2] = col >> 8
                screen[(fy*w + fx)*2+1] = col

    tft.image( 0, 0, w-1, h-1, screen )

